<?phpclass Functs{	// Database Connection	function Connection1(){				global $link1; 		 		// Si la connexion à la BD n'est pas faite 		if (!$link1) { 			$mysqlserver = 'localhost'; 			$mysqlloggin = 'root'; 			$mysqlpassword = ''; 			$db = 'woostore'; 			try { 				$link1 = mysql_connect($mysqlserver, $mysqlloggin, $mysqlpassword); 				mysql_select_db($db,$link1); 			} 			catch(Exception $e) { 				echo "Connection Impossible"; 			} 		} 		return $link1; 	} 	// Title to be used in 'alt' and 'tilte' 		function TitleFiled($chain){					$chain=str_replace( array('à','á','â','ã','ä', 'ç', 'è','é','ê','ë', 'ì','í','î','ï','ñ', 'ò','ó','ô','õ','ö','ù','ú','û','ü','ý','ÿ','À','Á','Â','Ã','Ä','Ç', 'È','É','Ê','Ë','Ì','Í','Î','Ï','Ñ','Ò','Ó','Ô','Õ','Ö', 'Ù','Ú','Û','Ü','Ý'), 							array('a','a','a','a','a','c','e','e','e','e','i','i','i','i','n','o','o','o','o','o','u','u','u','u','y','y','A','A','A','A','A','C','E','E','E','E','I','I','I','I','N','O','O','O','O','O','U','U','U','U','Y'), $chain); // Replacing accents		$chain=preg_replace("/[^0-9a-zA-Z]/", " ", trim($chain)); // Replacing all special char by space		$chain=preg_replace("/[-]{2,}/", "-", $chain); // Replace multiple dashes by one only		return $chain; // Return chain	}			// Count number of row in database to display	function RowPaginationTotalSubCategoriePage($sub){					$sql = mysql_query("SELECT COUNT(*) AS total FROM wp_posts where 1", $this->Connection1());		$k=mysql_fetch_assoc($sql);		return $k;	}		// Select a single page data from your database using limit clause 	function RowSubCategoriePaginationAjaxQuery($p,$nm){					$sql = mysql_query("SELECT * 							FROM  wp_posts where 1 order by post_date DESC LIMIT ".$p." , ".$nm."", $this->Connection1());		return $sql;	}	function pagination($current_page, $nb_pages){				$around=3;		$firstlast=1;		$pagination = '';		if ( $nb_pages > 1 ) {				// Previous Link			if ( $current_page > 1 )				$pagination .= '<a href="javascript:void(0)" onclick="pagination('.($current_page-1).'); return false;" style="color:#ffffff;">									<li id="'.($current_page-1).'" style="width: 100px;">&laquo; Previous</li>								</a>';			else				$pagination .= '<li style="width: 100px; background-color: #006699;">&laquo; Previous</li>';				// First Link			for ( $i=1 ; $i<=$firstlast ; $i++ ) {				$pagination .= ' ';				$pagination .= ($current_page==$i) ? '<a href="javascript:void(0)" onclick="pagination('.$i.'); return false;" style="color:#ffffff;"><li id="'.$i.'" style="background-color: #006699;"></a>'.$i.'</li>' : '<a href="javascript:void(0)" onclick="pagination('.$i.'); return false;" style="color:#ffffff;"><li id="'.$i.'">'.$i.'</li></a>';			}				// ... After first page ?			if ( ($current_page-$around) > $firstlast+1 )				$pagination .= '<li>&hellip;</li>';				// We buckle around the current page			$start = ($current_page-$around)>$firstlast ? $current_page-$around : $firstlast+1;			$end = ($current_page+$around)<=($nb_pages-$firstlast) ? $current_page+$around : $nb_pages-$firstlast;			for ( $i=$start ; $i<=$end ; $i++ ) {				$pagination .= ' ';				if ( $i==$current_page )					$pagination .= '<a href="javascript:void(0)" onclick="pagination('.$i.'); return false;" style="color:#ffffff;"><li id="'.$i.'" style="background-color: #006699;">'.$i.'</li></a>';				else					$pagination .= '<a href="javascript:void(0)" onclick="pagination('.$i.'); return false;" style="color:#ffffff;"><li id="'.$i.'" >'.$i.'</li></a>';			}				// ... Before last page ?			if ( ($current_page+$around) < $nb_pages-$firstlast )				$pagination .= '<li>&hellip;</li>';				// Last Link			$start = $nb_pages-$firstlast+1;			if( $start <= $firstlast ) $start = $firstlast+1;			for ( $i=$start ; $i<=$nb_pages ; $i++ ) {				$pagination .= ' ';				$pagination .= ($current_page==$i) ? '<a href="javascript:void(0)" onclick="pagination('.$i.'); return false;" style="color:#ffffff;"><li id="'.$i.'" style="background-color: #006699;">'.$i.'</li></a>' : '<a href="javascript:void(0)" onclick="pagination('.$i.'); return false;" style="color:#ffffff;"><li id="'.$i.'">'.$i.'</li></a>';			}				// Next Link			if ( $current_page < $nb_pages )				$pagination .= '<a href="javascript:void(0)" onclick="pagination('.($current_page+1).'); return false;" style="color:#ffffff;"><li id="'.($current_page+1).'" style="width: 100px;">Next &raquo;</li></a>';			else				$pagination .= ' <li style="width: 100px; background-color: #006699;">Next &raquo;</li>';		}		return $pagination;	}		}?>